import './style.css';
import {
  Button,
  ButtonGroup
} from '@shopify/polaris';

class TableCommunes extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      communes: this.props.communes,
    }
  }

  renderTableData() {
    return this.state.communes.map((commune, index) => {
       const { id, name, precio, text } = commune //destructuring
       return (
          <div className="tableCommune-row" key={id}>
            <div className="tableCommune-col col1">{name}</div>
            <div className="tableCommune-col col2">{precio}</div>
            <div className="tableCommune-col col3">{text}</div>
            <div className="tableCommune-col col4">
            <ButtonGroup segmented>
              <Button>Borrar</Button>
              <Button>Edit/Guardar</Button>
            </ButtonGroup>
            </div>
          </div>
       )
    })
  }
  render() {
    return (
      <div className="tableCommune-container">
        <header className="tableCommune-row">
          <div class="tableCommune-col col1">Comuna</div>
          <div class="tableCommune-col col2">Precio</div>
          <div class="tableCommune-col col3">Pol√≠tica de despacho</div>
          <dic class="tableCommune-col col4">Acciones</dic>
        </header>
        <div className="tableCommune-rows">
          {this.renderTableData()}
        </div>
      </div>
    );
  }
}

export default TableCommunes;
